import { useState } from "react";
import './buttonSection.css'

export default function Button() {
    const [isOpen, setIsOpen] = useState(false);

    function toggle() {
        setIsOpen((isOpen) =>!isOpen);
    }

    return (
        <div>
            <div className="container" hidden={isOpen}>
                <button className="button" onClick={toggle}>Add New Expense</button>
            </div>
            <div className="container" hidden={!isOpen}>
                <form>
                    <label for="ftitle">Title</label><br />
                    <input type="text" id="ftitle"></input><br />
                    <label for="famount">Amount</label><br />
                    <input type="text" id="famount"></input><br />
                    <label for="fdate">Date</label><br />
                    <input type="date" id="fdate"></input>
                </form>
                <div className="buttons-box">
                    <button className="box-button" onClick={toggle}>Cancel</button>
                    <button ty className="box-button">Add Expense</button>
                </div>
            </div>
        </div>
    );
}
