import { useState } from "react";
import 'button'

export default function Button() {
    const [isOpen, setIsOpen] = useState(false);

    function toggle() {
        setIsOpen((isOpen) =>!isOpen);
    }

    return (
        <div>
            {isOpen && <p>Form</p>}
            <button onClick={toggle}>Toggle show</button>
        </div>
    );
}
