import { useState } from "react";
import './buttonSection.css'

export default function Button() {
    const [isOpen, setIsOpen] = useState(false);

    function toggle() {
        setIsOpen((isOpen) =>!isOpen);
    }

    function handleSubmit() {

    }

    return (
        <div>
            <div className="container" hidden={isOpen}>
                <button className="button" onClick={toggle}>Add New Expense</button>
            </div>
            <div className="container" hidden={!isOpen}>
                <form onSubmit={handleSubmit} className="form-container">
                    <div className="form-element">
                        <label for="ftitle" className="form-label">Title</label><br />
                        <input type="text" id="ftitle" className="form-input"></input><br />
                    </div>
                    <div className="form-element">
                        <label for="famount" className="form-label">Amount</label><br />
                        <input type="text" id="famount" className="form-input"></input><br />
                    </div>
                    <div className="form-element">
                        <label for="fdate" className="form-label">Date</label><br />
                        <input type="date" id="fdate" className="form-input"></input>
                    </div>
                </form>
                <div className="buttons-box">
                    <button className="box-button" onClick={toggle}>Cancel</button>
                    <button type="submit" className="box-button" >Add Expense</button>
                </div>
            </div>
        </div>
    );
}
